insert  into `#__jed_email_templates`(`id`,`title`,`subject`,`template`,`email_type`,`created_by`,`modified_by`,`created`,`modified`,`state`) values (1,'Notification of Report','Dummy Subject','<p>Dear Developer I am writing to you to know that a report has been made to Joomla! regarding your extension Please investigate and report back to us using this link Best Wishes The Joomla Extension Directory Team</p>',2,769,652,'2021-02-10 12:09:44','2021-05-24 23:44:08',1);
insert  into `#__jed_email_templates`(`id`,`title`,`subject`,`template`,`email_type`,`created_by`,`modified_by`,`created`,`modified`,`state`) values (2,'Review Report Received','Review Report Received','<p>Your report has been successfully received and a ticket has been opened. A team member will look into the issue and you should receive an update to your ticket within the next 3 business days.</p>',1,652,652,'2021-05-13 14:37:13','2021-05-13 14:37:13',1);
insert  into `#__jed_email_templates`(`id`,`title`,`subject`,`template`,`email_type`,`created_by`,`modified_by`,`created`,`modified`,`state`) values (3,'Listing Submission Received','Listing Submission Received','<p>Your submission has been successfully received.</p>\r\n<p>Your listing will be screened by an extension specialist. If it passes all of our requirements it will be published. If it does not pass the initial screening you will receive a notice outlining the errors found and the next steps to take. If you checked the field \"My extension includes external libraries\" it will be manually screened in the order it was received by a team member.</p>\r\n<p><strong>NOTE</strong>: Our extension specialists review the extensions with <strong>JED Checker</strong>.&nbsp;The JED checker can be used to identify which files have issues - <a href=\"http://extensions.joomla.org/extension/jedchecker\" rel=\"alternate\">http://extensions.joomla.org/extension/jedchecker</a>.&nbsp;<strong>You are still on time to do a last check up</strong>.</p>\r\n<hr />\r\n<h2>Support the Joomla Community. Sponsor Us!</h2>\r\n<p><a href=\"https://community.joomla.org/sponsorship-campaigns.html\"><img src=\"https://extensions.joomla.org/images/social-images/joomla-community-sponsorships.jpg\" alt=\"Sponsor Us!\" /></a></p>\r\n<p><a href=\"https://community.joomla.org/sponsorship-campaigns.html\" target=\"_blank\" rel=\"noopener noreferrer\"><strong>Community Sponsorships</strong></a> allow those looking to support the project with smaller sponsorship amounts. With community sponsorships available from $5, every little bit helps to make Joomla! better.</p>\r\n<p><a class=\"btn btn-primary\" href=\"https://community.joomla.org/community-2017.html\" target=\"_blank\" rel=\"noopener noreferrer\"> Sponsor Now! </a></p>\r\n<hr />\r\n<ul>\r\n<li><a href=\"http://extensions.joomla.org/index.php?option=com_jed&amp;view=extension&amp;layout=edit&amp;amp;id={fab_qs}extension_id{/fab_qs}\">Back to edit {fab_qs}core_title{/fab_qs}</a></li>\r\n<li><a href=\"http://extensions.joomla.org/browse/my-extensions\">View my listings</a></li>\r\n</ul>',1,652,652,'2021-05-13 14:37:13','2021-05-13 14:37:13',1);
insert  into `#__jed_email_templates`(`id`,`title`,`subject`,`template`,`email_type`,`created_by`,`modified_by`,`created`,`modified`,`state`) values (4,'Listing Update Received','Listing Update Received','<p>Your extension&nbsp;has been successfully updated.</p>\r\n<hr />\r\n<h2>Support the Joomla Community. Sponsor Us!</h2>\r\n<p><a href=\"https://community.joomla.org/sponsorship-campaigns.html\"><img src=\"https://extensions.joomla.org/images/social-images/joomla-community-sponsorships.jpg\" alt=\"Sponsor Us!\" /></a></p>\r\n<p><a href=\"https://community.joomla.org/sponsorship-campaigns.html\" target=\"_blank\" rel=\"noopener noreferrer\"><strong>Community Sponsorships</strong></a> allow those looking to support the project with smaller sponsorship amounts. With community sponsorships available from $5, every little bit helps to make Joomla! better.</p>\r\n<p><a class=\"btn btn-primary\" href=\"https://community.joomla.org/community-2017.html\" target=\"_blank\" rel=\"noopener noreferrer\"> Sponsor Now! </a></p>\r\n<hr />\r\n<p><a class=\"btn btn-info\" href=\"https://extensions.joomla.org/index.php?option=com_jed&amp;view=extension&amp;layout=edit&amp;amp;id={fab_qs}extension_id{/fab_qs}&amp;Itemid=134\">Back to edit {fab_qs}core_title{/fab_qs}</a> <a class=\"btn btn-primary\" href=\"https://extensions.joomla.org/index.php?option=com_jed&amp;view=extension&amp;layout=default&amp;id={fab_qs}extension_id{/fab_qs}&amp;Itemid=145\">Continue to {fab_qs}core_title{/fab_qs}</a> <a class=\"btn\" href=\"https://extensions.joomla.org/browse/my-extensions\">View my listings</a></p>',1,652,652,'2021-05-13 14:37:13','2021-05-13 14:37:13',1);
insert  into `#__jed_email_templates`(`id`,`title`,`subject`,`template`,`email_type`,`created_by`,`modified_by`,`created`,`modified`,`state`) values (5,'Extension Report Received','Extension Report Received','<p>Your report has been successfully received and a ticket has been opened. A team member will look into the issue and you should receive an update to your ticket within the next 3 business days.</p>',1,652,652,'2021-05-13 14:38:15','2021-05-13 14:38:15',1);
insert  into `#__jed_email_templates`(`id`,`title`,`subject`,`template`,`email_type`,`created_by`,`modified_by`,`created`,`modified`,`state`) values (6,'Extension - JED Checker Errors','Extension - JED Checker Errors','<p>Hi,<br /><br />Your extension has been flagged with [XXX] errors. Please correct these issues and upload an amended zip file to your listing. The JED checker can be used to identify which files have issues - http://extensions.joomla.org/extension/jedchecker<br /><br />When you\'ve finished your changes, please open a ticket under “New Listing Support” and ask for your extension to be checked again.<br /><br />Kind Regards</p>',2,652,652,'2021-05-13 14:40:36','2021-05-13 14:40:36',1);
insert  into `#__jed_email_templates`(`id`,`title`,`subject`,`template`,`email_type`,`created_by`,`modified_by`,`created`,`modified`,`state`) values (7,'Extension: ER1 - error_reporting(0) Found','Extension: ER1 - error_reporting(0) Found','<p>Hi,<br /><br />Please remove any use of error_reporting(0) from your files, use of this is discouraged because Joomla provides an error reporting options in the Global Configuration. <br /><br />When you\'ve finished your changes, please upload an amended zip file to your listing and then open a ticket under “New Listing Support” and ask for your extension to be checked again.<br /><br />Kind Regards</p>',2,652,652,'2021-05-13 14:40:36','2021-05-13 14:40:36',1);
insert  into `#__jed_email_templates`(`id`,`title`,`subject`,`template`,`email_type`,`created_by`,`modified_by`,`created`,`modified`,`state`) values (8,'Extension: NM1 - Install Name Doesn\'t Match Listing Name','Extension: NM1 - Install Name Doesn\'t Match Listing Name','<p>Hi,<br /><br />The install name of your extension doesn\'t match the listing name. Please update the name in your xml/language files or request for the listing name to be changed.<br /><br />When you\'ve finished your changes, please upload an amended zip file to your listing and then open a ticket under “New Listing Support” and ask for your extension to be checked again, stating which of the above options you prefer.<br /><br />Kind Regards</p>',2,652,652,'2021-05-13 14:40:36','2021-05-13 14:40:36',1);
insert  into `#__jed_email_templates`(`id`,`title`,`subject`,`template`,`email_type`,`created_by`,`modified_by`,`created`,`modified`,`state`) values (9,'Extension: LK2 - Invalid Download Link','Extension: LK2 - Invalid Download Link','<p>Hi,<br /><br />Please update the download link so that it complies with the JED Rules as follows:</p>\r\n<ul>\r\n<li>Download links must point directly to the download or product page.</li>\r\n<li>You may point directly to the file itself if registration isn’t required.</li>\r\n<li>Download links may not point to \"Extension Installers\".</li>\r\n<li>If you offer multiple versions of an extension (for example, a Paid version and a Free version) you must only point the download link on your listing to a page that the version promoted is the one displayed on the JED.</li>\r\n<li>Links pointing to distribution sites that also distribute non-GPL Joomla extensions will not be accepted.</li>\r\n</ul>\r\n<p>When you\'ve finished your changes, please open a ticket under “New Listing Support” and ask for your extension to be checked again.<br /><br />Kind Regards</p>',2,652,652,'2021-05-13 14:40:36','2021-05-13 14:40:36',1);
insert  into `#__jed_email_templates`(`id`,`title`,`subject`,`template`,`email_type`,`created_by`,`modified_by`,`created`,`modified`,`state`) values (10,'Extension: ZP1 - Zipfile Issues','Extension: ZP1 - Zipfile Issues','<p>Hi,</p>\r\n<p>An issue has been detected with the zip file of your extension. Please ensure that the zip file of your extension has:</p>\r\n<ul>\r\n<li>Been packaged properly and is not corrupt and can be opened.</li>\r\n<li>Been uploaded correctly to your listing.</li>\r\n</ul>\r\n<p>When you\'ve done this, please upload an amended zip file to your listing and then open a ticket under “New Listing Support” and ask for your extension to be checked again.<br /><br />Kind Regards</p>',2,652,652,'2021-05-13 14:40:36','2021-05-13 14:40:36',1);
insert  into `#__jed_email_templates`(`id`,`title`,`subject`,`template`,`email_type`,`created_by`,`modified_by`,`created`,`modified`,`state`) values (11,'Extension: NM2 - Extension Specific Naming Issue','Extension: NM2 - Extension Specific Naming Issue','<p>Hi,<br /><br />The name of your extension doesn’t comply with the JED naming conventions - extension specific listings should have a name in the form “{Extension Name} for {Parent Extension}”.<br /><br />Please refer to the JED Naming Conventions: http://extensions.joomla.org/support/knowledgebase/item/extension-names and choose a compliant name for your listing. This new name needs to be reflected in the files of your extension, specifically in the xml and language files.<br /><br />When you’ve finished your changes, please upload an amended zip file to your listing and then open a ticket under “New Listing Support” and include the new name for your listing. A member of the team will then update the listing name to match the install name of your extension.<br /><br />Kind Regards,</p>',2,652,652,'2021-05-13 14:40:36','2021-05-13 14:40:36',1);
insert  into `#__jed_email_templates`(`id`,`title`,`subject`,`template`,`email_type`,`created_by`,`modified_by`,`created`,`modified`,`state`) values (12,'Extension: TM2 - Use of the Joomla Trademark','Extension: TM2 - Use of the Joomla Trademark','<p><span style=\"font-weight: 400;\">Hi,</span></p>\r\n<p><span style=\"font-weight: 400;\">The name of your extension doesn’t comply with the JED naming conventions - an extension name cannot include the word Joomla, unless in the form “{Extension Name} for Joomla”.</span></p>\r\n<p><span style=\"font-weight: 400;\">Please refer to the JED Naming Conventions: </span><a href=\"http://extensions.joomla.org/support/knowledgebase/item/extension-names\"><span style=\"font-weight: 400;\">http://extensions.joomla.org/support/knowledgebase/item/extension-names</span></a><span style=\"font-weight: 400;\"> and choose a compliant name for your listing. This new name needs to be reflected in the files of your extension, specifically in the xml and language files.</span></p>\r\n<p><span style=\"font-weight: 400;\">Alternatively, if you wish to use the word Joomla in any other form, please see the following links for more information </span><a href=\"https://tm.joomla.org/joomla-name-and-logo-use\"><span style=\"font-weight: 400;\">https://tm.joomla.org/joomla-name-and-logo-use</span></a><span style=\"font-weight: 400;\"> and </span><a href=\"https://tm.joomla.org/trademark\"><span style=\"font-weight: 400;\">https://tm.joomla.org/trademark</span></a><span style=\"font-weight: 400;\">. You can submit your request via the Trademark Contact Center link. If your request is approved, you must inform the JED of the decision.</span></p>\r\n<p><span style=\"font-weight: 400;\">When you’ve finished your changes, please open a ticket under “New Listing Support” and include the new name for your listing. A member of the team will then update the listing name to match the install name of your extension.</span></p>\r\n<p><span style=\"font-weight: 400;\">Kind Regards,</span></p>',2,652,652,'2021-05-13 14:40:36','2021-05-13 14:40:36',1);
insert  into `#__jed_email_templates`(`id`,`title`,`subject`,`template`,`email_type`,`created_by`,`modified_by`,`created`,`modified`,`state`) values (13,'Extension - Hidden Files or Folders','Extension - Hidden Files or Folders','<p>Hi,</p>\r\n<p>The zip file of your extension includes some hidden files/folders (e.g.__MACOSX folders or .DS_Store files), that can cause issues on some hosting providers. Please remove all occurrences of these files/folders and upload an amended zip file to your listing.</p>\r\n<p>When you\'ve done this, please open a ticket under “New Listing Support” and ask for your extension to be checked again.</p>\r\n<p>Kind Regards</p>',2,652,652,'2021-05-13 14:40:36','2021-05-13 14:40:36',1);
insert  into `#__jed_email_templates`(`id`,`title`,`subject`,`template`,`email_type`,`created_by`,`modified_by`,`created`,`modified`,`state`) values (14,'Extension: NM3 - Non-Permitted Words in Name','Extension: NM3 - Non-Permitted Words in Name','<p>Hi,</p>\r\n<p>The name of your extension doesn’t comply with the JED naming conventions - an extension name cannot include the words: component, module, plugin or extension</p>\r\n<p>Please refer to the JED Naming Conventions: http://extensions.joomla.org/support/knowledgebase/item/extension-names and choose a compliant name for your listing. This new name needs to be reflected in the files of your extension, specifically in the xml and language files.</p>\r\n<p>When you’ve finished your changes, please upload an amended zip file to your listing and then open a ticket under “New Listing Support” and include the new name for your listing. A member of the team will then update the listing name to match the install name of your extension.</p>\r\n<p>Kind Regards</p>',2,652,652,'2021-05-13 14:40:36','2021-05-13 14:40:36',1);
insert  into `#__jed_email_templates`(`id`,`title`,`subject`,`template`,`email_type`,`created_by`,`modified_by`,`created`,`modified`,`state`) values (15,'Extension - Plugin Install Name','Extension - Plugin Install Name','<p><span style=\"font-weight: 400;\">Hi,</span></p>\r\n<p><span style=\"font-weight: 400;\">The name of your plugin does not comply with the JED naming conventions - plugins should have a name in the form “{Type} - {Extension Name}”.</span></p>\r\n<p><span style=\"font-weight: 400;\">When you’ve finished your changes and have uploaded an amended zip file to your listing, please open a ticket under “New Listing Support” and ask for it to be checked again.</span></p>\r\n<p><span style=\"font-weight: 400;\">Kind Regards</span></p>',2,652,652,'2021-05-13 14:40:36','2021-05-13 14:40:36',1);
insert  into `#__jed_email_templates`(`id`,`title`,`subject`,`template`,`email_type`,`created_by`,`modified_by`,`created`,`modified`,`state`) values (16,'Extension - Backlink Issues','Extension - Backlink Issues','<p><span style=\"font-weight: 400;\">Hi,</span></p>\r\n<p><span style=\"font-weight: 400;\">Your extension contains backlinks that are not inline with the JED TOS. Extensions listed on the JED are permitted to insert backlinks to the developers distribution site, which will render in the HTML output of the extension. The JED has specific rules regarding backlinks:</span></p>\r\n<ul>\r\n<li><span style=\"font-weight: 400;\">Users must be able to remove the backlink (by editing the code)</span></li>\r\n<li><span style=\"font-weight: 400;\">Base64 or any other method to obfuscate the backlink is not permitted</span></li>\r\n<li><span style=\"font-weight: 400;\">No more than one backlink can be inserted</span></li>\r\n<li><span style=\"font-weight: 400;\">The backlink can only point to the developer\'s distribution site</span></li>\r\n</ul>\r\n<p><span style=\"font-weight: 400;\">See this page for more information. <a href=\"http://extensions.joomla.org/support/knowledgebase/item/backlinks\">http://extensions.joomla.org/support/knowledgebase/item/backlinks</a> </span></p>\r\n<p><span style=\"font-weight: 400;\">Please make the necessary amendments to your files and then upload an amended zipfile to your listing. When you\'ve done this, open a ticket under \"New Listing Support\" and ask for it to be checked again.</span></p>\r\n<p><span style=\"font-weight: 400;\">Kind Regards</span></p>',2,652,652,'2021-05-13 14:40:36','2021-05-13 14:40:36',1);
insert  into `#__jed_email_templates`(`id`,`title`,`subject`,`template`,`email_type`,`created_by`,`modified_by`,`created`,`modified`,`state`) values (17,'Extension: LC2 - Paid Listing without License Link','Extension: LC2 - Paid Listing without License Link','<p><span style=\"font-weight: 400;\">Hi,</span></p>\r\n<p><span style=\"font-weight: 400;\">You have listed your extension as \"Paid\", but have not added a link to the license page on your website. This is required according to the JED TOS. As the JED grows, new opportunities and questions arise. One issue that plagues the JED and its users is arriving at a site from a listing that is \"Paid\" only to find out that additional restrictions have been placed on the extension. To help monitor this, if your listing is Paid, you must include a link to your terms of service or license agreement.<br /></span></p>\r\n<p><span style=\"font-weight: 400;\">Please update the link and then open a ticket under \"New Listing Support\" and ask for it to be checked again.</span></p>\r\n<p><span style=\"font-weight: 400;\">Kind Regards</span></p>',2,652,652,'2021-05-13 14:40:36','2021-05-13 14:40:36',1);
insert  into `#__jed_email_templates`(`id`,`title`,`subject`,`template`,`email_type`,`created_by`,`modified_by`,`created`,`modified`,`state`) values (18,'Extension - Listed on VEL','Extension - Listed on VEL','<p><span style=\"font-weight: 400;\">Hi,</span></p>\r\n<p><span style=\"font-weight: 400;\">Your extension has been listed in the the VEL. JED requires the VEL resolved link before your extension can be republished.</span></p>\r\n<p><span style=\"font-weight: 400;\">Please provide us with the VEL resolved link by opening a ticket under \"Unpublished Support\".<br /></span></p>\r\n<p><span style=\"font-weight: 400;\">Kind Regards</span></p>',2,652,652,'2021-05-13 14:40:36','2021-05-13 14:40:36',1);
insert  into `#__jed_email_templates`(`id`,`title`,`subject`,`template`,`email_type`,`created_by`,`modified_by`,`created`,`modified`,`state`) values (19,'Extension - Broken Links','Extension - Broken Links','<p><span style=\"font-weight: 400;\">Hi,</span></p>\r\n<p><span style=\"font-weight: 400;\">Your extension has been unpublished because we have found that one or more of your links on your listing is not functioning. Please correct this link so that it goes to the correct URL.</span></p>\r\n<p><span style=\"font-weight: 400;\">When you have done this, please open a ticket under \"Unpublished Support\".<br /></span></p>\r\n<p><span style=\"font-weight: 400;\">Kind Regards</span></p>',2,652,652,'2021-05-13 14:40:36','2021-05-13 14:40:36',1);
insert  into `#__jed_email_templates`(`id`,`title`,`subject`,`template`,`email_type`,`created_by`,`modified_by`,`created`,`modified`,`state`) values (20,'Ticket - Misdirected Support Request','Ticket - Misdirected Support Request','<p><span style=\"font-weight: 400;\">Hi,</span></p>\r\n<p><span style=\"font-weight: 400;\">This ticket system is for developers of extensions listed on JED - we are unable to provide support for your issue. For support questions about how to use an extension, please contact the developer directly via their website.</span></p>\r\n<p><span style=\"font-weight: 400;\">Kind Regards</span></p>',2,652,652,'2021-05-13 14:40:36','2021-05-13 14:40:36',1);
insert  into `#__jed_email_templates`(`id`,`title`,`subject`,`template`,`email_type`,`created_by`,`modified_by`,`created`,`modified`,`state`) values (21,'Ticket - Not Enough Detail','Ticket - Not Enough Detail','<p><span style=\"font-weight: 400;\">Hi,</span></p>\r\n<p><span style=\"font-weight: 400;\">Your ticket does not include enough information for us to be able to look into your issue. If you still have an issue, please open a new ticket and include as much information as possible, including the name of the extension and issue details.</span></p>\r\n<p><span style=\"font-weight: 400;\">Kind Regards</span></p>',2,652,652,'2021-05-13 14:40:36','2021-05-13 14:40:36',1);
insert  into `#__jed_email_templates`(`id`,`title`,`subject`,`template`,`email_type`,`created_by`,`modified_by`,`created`,`modified`,`state`) values (22,'Extension: LC1 - Licensing Violation','Extension: LC1 - Licensing Violation','<p>Hi,</p>\r\n<p>An issue has been found with the licensing of your extension.</p>\r\n<p>Every listing must comply with the current GPL License that Joomla is distributed as. Currently, Joomla is distributed using GPL v2. There are other licenses that are compatible with the GPL v2, and those are acceptable as well. Additionally, the JED does not allow \"additional restrictions\" on top of the GPL. For example, you cannot limit the usage of your extension to limited number of domains. You may, however, sell \"support\" based on a limited number of domains.</p>\r\n<p>Please make the necessary amendments to ensure that your extension complies with the above restrictions. Also ensure that you have updated the relevant pages on your website, including the extension page and licensing page to reflect the changes that you have made.</p>\r\n<p>When you have finished your changes, please upload an amended zip file to your listing and then open a ticket under \"Current Listing Support\" and ask for your extension to be checked again.</p>\r\n<p>Kind Regards</p>',2,652,652,'2021-05-13 14:40:36','2021-05-13 14:40:36',1);
insert  into `#__jed_email_templates`(`id`,`title`,`subject`,`template`,`email_type`,`created_by`,`modified_by`,`created`,`modified`,`state`) values (23,'Extension: LC3 - License Link does not Mention Extensions','Extension: LC3 - License Link does not Mention Extensions','<p>Hi,</p>\r\n<p>The license link that you have provided does not mention extensions. Your license page should reference specifically how your extensions are licensed.</p>\r\n<p>Please make the necessary changes to this page on your website and then open a ticket under \"Current Listing Support\" and ask for it to be checked again.</p>\r\n<p>Kind Regards</p>',2,652,652,'2021-05-13 14:40:36','2021-05-13 14:40:36',1);
insert  into `#__jed_email_templates`(`id`,`title`,`subject`,`template`,`email_type`,`created_by`,`modified_by`,`created`,`modified`,`state`) values (24,'Extension: LC4 - Invalid License Type','Extension: LC4 - Invalid License Type','<p>Hi,</p>\r\n<p>Your extension has been found to have an invalid license type. Extensions are required to be GNU/GPL or AGPL licensed. <em><strong>LGPL is for library extensions only.</strong></em> Any other license type is unacceptable.</p>\r\n<p>Please make the necessary changes to your website and your extension, upload an amended zip file to your listing and then open a ticket under \"Current Listing Support\" and ask for it to be checked again.</p>\r\n<p>Kind Regards</p>',2,652,652,'2021-05-13 14:40:36','2021-05-13 14:40:36',1);
insert  into `#__jed_email_templates`(`id`,`title`,`subject`,`template`,`email_type`,`created_by`,`modified_by`,`created`,`modified`,`state`) values (25,'Extension: US1 - Update Server Requirement','Extension: US1 - Update Server Requirement','<p><span style=\"font-weight: 400;\">Hi,</span></p>\r\n<p><span style=\"font-weight: 400;\">Extensions uploaded to JED after 10th January 2017 are required to implement the Joomla! Update System as detailed in this documentation page: </span><a href=\"support/knowledgebase/item/joomla-update-system-requirement\"><span style=\"font-weight: 400;\">https://extensions.joomla.org/support/knowledgebase/item/joomla-update-system-requirement</span></a></p>\r\n<p><span style=\"font-weight: 400;\">Please make the necessary changes to your extension and upload an amended zipfile to your listing. Also, please ensure that you have checked the ‘Joomla Update System’ checkbox on the edit page of your listing.</span></p>\r\n<p><span style=\"font-weight: 400;\">When you’ve finished your changes please open a ticket under “New Listing Support” and ask for it to be checked again.</span></p>\r\n<p><span style=\"font-weight: 400;\">Kind Regards,</span></p>\r\n<p><span style=\"font-weight: 400;\">[Your Name]</span></p>',2,652,652,'2021-05-13 14:40:36','2021-05-13 14:40:36',1);
insert  into `#__jed_email_templates`(`id`,`title`,`subject`,`template`,`email_type`,`created_by`,`modified_by`,`created`,`modified`,`state`) values (26,'Extension: PE1 - Under Investigation','Extension: PE1 - Under Investigation','<p>Hi,</p>\r\n<p>Your extension has been tagged as under investigation.&nbsp;</p>\r\n<p>Please open a ticket under \"Current Listing Support\" to contact the JED team.</p>\r\n<p>Best Regards</p>',2,652,652,'2021-05-13 14:40:36','2021-05-13 14:40:36',1);
